// setting the carousel time
$(".carousel").carousel({ interval: 4e3 });// carousel changes in every 4sec

var isHearted = [!1, !1, !1, !1];

// check if the product is hearted or not
const changeColor = (a) => {
  isHearted[a]
    ? ((isHearted[a] = !1),
      (document.getElementById(`heart${a}`).style.color = "#4a4a4a"))
    : ((isHearted[a] = !0),
      (document.getElementById(`heart${a}`).style.color = "#ffcb2e"));
};
